sys_script_client.script:
function onLoad() {
  var sysId = g_form.getUniqueValue();
  if (!sysId) return;

  var ga = new GlideAjax('EventUtilsClient');
  ga.addParam('sysparm_name', 'fetchEventDetails');
  ga.addParam('sysparm_event_id', sysId);
  ga.getXMLAnswer(function(response) {
    if (!response) {
      g_form.showFieldMsg('event_name',
        'Unable to load event details.',
        'error');
      return;
    }

    var details = JSON.parse(response);
    g_form.setValue('event_name',     details.name);
    g_form.setValue('event_date',     details.date);
    g_form.setValue('location',       details.location);
    g_form.setValue('status',         details.status);
  });
}
