sys_script_include.script:
var FeedbackUtils = Class.create();
FeedbackUtils.prototype = {
    initialize: function(eventid) {
        this.eventid = eventid;
    },
	getFeedback:function(id){
        var res = [];
        var feedback = new GlideRecord('x_1790002_event_ma_feedback');
        feedback.addQuery('number', id);
        feedback.query();
        if (feedback.next()) {
            res.push({
                id: feedback.getDisplayValue(),
				rating : feedback.getValue('rating'),
            });}
            return JSON.stringify(res);

    },
	checkFeedback:function(id){
        var feedback = new GlideRecord('x_1790002_event_ma_feedback');
        feedback.addQuery('number', id);
        feedback.query();
        if (feedback.next()) {
			if(feedback.getValue('rating')== 'excellent'||feedback.getValue('rating')== 'good')
			{
				return 'positive';
			}
		}
		return 'negative';
		},
		getEventFeedback:function(){
        var res = [];
        var feedback = new GlideRecord('x_1790002_event_ma_feedback');
        feedback.addQuery('event_id.number', this.eventid);
        feedback.query();
        if (feedback.next()) {
            res.push({
                id: feedback.getDisplayValue(),
				rating : feedback.getValue('rating'),
            });}
            return JSON.stringify(res);

    },



    type: 'FeedbackUtils'
};
