sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var body = request.body.data;
    var firstName = body.first_name;
    var lastName = body.last_name;
    var email = body.email;
    var contactNumber = body.contact_number;
    var eventId = body.event_id;

    // Basic validation
    if (!firstName || !lastName || !email || !contactNumber || !eventId) {
        response.setStatus(400);
        return { status: 'error', message: 'All fields are required.' };
    }

    // Create new attendee record (assuming 'u_attendee' is the table)
    var attendeeGR = new GlideRecord('x_1790002_event_ma_attendee');
    attendeeGR.initialize();
    attendeeGR.first_name = firstName;
    attendeeGR.last_name = lastName;
    attendeeGR.email = email;
    attendeeGR.contact_number = contactNumber;
    attendeeGR.event = eventId;
    var sysId = attendeeGR.insert();

    // Success response
    response.setStatus(201);
    return {
        status: 'success',
        attendee_sys_id: sysId,
        message: 'Attendee record created successfully.'
    };
})(request, response);
